const morse = require('./morse');

const input = '1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011';
const output = 'HEY JUDE';
const input2 = "11001100110011000000110000001100111111001100110000001100111111001100110000001111110011111100111111"
const output2 = "HELLO"
const input3 = "11001100110011000000110000001100111111001100110000001100111111001100110000001111110011111100111111000000000000001100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011"
const output3 = "HELLO HEY JUDE"
const input4 = ''
const output4 = "VAYAN A LOS TACOS"
describe('Morse Tests', () => {

    test('expect length of hey jude', () => {
        expect(morse(input).length).toBe(output.length);
    });

    test('expect HEY JUDE', () => {
        expect(morse(input)).toBe(output);
    });

    test('expect HELLO', () => {
        expect(morse(input2)).toBe(output2);
    });

    test('expect HELLO HEY JUDE', () => {
        expect(morse(input3)).toBe(output3);
    });

    test('expect VAYAN A LOS TACOS', () => {
        expect(morse(input4)).toBe(output4);
    });
    

});
